    public View onCreateView(@NonNull LayoutInflater inflater,
                             ViewGroup container, Bundle savedInstanceState) {
        doctorsViewModel =
                ViewModelProviders.of(this).get(DoctorsViewModel.class);
        View root = inflater.inflate(R.layout.fragment_doctors, container, false);
        // Create a List from String Array elements
        // Create an ArrayAdapter from List
        DoctorsAdapter doctorsAdapter = new DoctorsAdapter(getActivity(), doctorDataList);

        ListView listView = root.findViewById(R.id.doctorList);
        listView.setAdapter(doctorsAdapter);

        // Set an item click listener for ListView
        listView.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {
                // Get the selected item text from ListView
                Doctor selectedItem = (Doctor) parent.getItemAtPosition(position);
                Intent intent = new Intent(getActivity(), DoctorDetailActivity.class);
                intent.putExtra("doctorName", selectedItem.getFirstName() + " " + selectedItem.getLastName());
                intent.putExtra("introduction", selectedItem.getIntroduction());
                startActivity(intent);
                System.out.println(selectedItem.getFirstName());
            }
        });
        return root;
    }

    class DoctorsAdapter extends ArrayAdapter<Doctor> {
        public DoctorsAdapter(Context context, List<Doctor> doctors) {
            super(context, 0, doctors);
        }

        @Override
        public View getView(int position, View convertView, ViewGroup parent) {
            // Get the data item for this position
            Doctor doctor = getItem(position);
            // Check if an existing view is being reused, otherwise inflate the view
            if (convertView == null) {
                convertView = LayoutInflater.from(getContext()).inflate(R.layout.doctor_item, parent, false);
            }
            // Lookup view for data population
            TextView tvName = convertView.findViewById(R.id.doctorName);
            TextView tvDepart = convertView.findViewById(R.id.department);
            // Populate the data into the template view using the data object
            tvName.setText(doctor.getLastName() + " " + doctor.getFirstName());
            tvDepart.setText(doctor.getDepartment());
            // Return the completed view to render on screen
            return convertView;
        }
    }
